CREATE TABLE userinfo(id int unique not null auto_increment primary key, OAuth varchar(255), email varchar(255) unique not null, password varchar(255) not null, name varchar(255) DEFAULT '名無し', signup_date datetime not null, safe_search int not null DEFAULT 0, NG_count int DEFAULT 0);
CREATE TABLE page_status(id int unique not null auto_increment primary key, title varchar(255) not null, URL varchar(8190) unique not null, regist_date datetime not null, last_update datetime, admin_user_id int, media varchar(30) not null, genre varchar(255) not null, dead_flag int DEFAULT 0, tag1 varchar(30), tag2 varchar(30), tag3 varchar(30), tag4 varchar(30), tag5 varchar(30), tag6 varchar(30), tag7 varchar(30), tag8 varchar(30), tag9 varchar(30), tag10 varchar(30));
CREATE TABLE individual_eval_template(num int unique not null auto_increment primary key, posted datetime not null, evaluator_id int unique not null, browse_time datetime not null, browse_purpose text not null, deliberate int DEFAULT 0, describe_eval text, recommend_good int, recommend_bad int, goodness_of_fit int not null, num_typo int not null, opt1 int, opt2 int, opt3 int, opt4 int, opt5 int, opt6 int, opt7 int, opt8 int, opt9 int, opt10 int);
CREATE TABLE individual_eval_comment_template(num int unique not null auto_increment primary key, posted datetime not null, commenter_id int not null, reply_eval_num int, reply_comment_num int, deliberate int DEFAULT 0, comment text not null, recommend_good int, recommend_bad int);
CREATE TABLE typo_template(num int unique not null auto_increment primary key, evaluator_id int not null, individual_eval_num int not null, incorrect varchar(255) not null, correct varchar(255) not null);
CREATE TABLE rating_item(num int unique not null auto_increment primary key, genre varchar(255) not null, media varchar(30) not null, opt1 int, opt2 int, opt3 int, opt4 int, opt5 int, opt6 int, opt7 int, opt8 int, opt9 int, opt10 int);
CREATE TABLE page_status_item(num int unique not null auto_increment primary key, genre varchar(255), media varchar(30));
CREATE TABLE all_NG_word(Lv1 varchar(255), Lv2 varchar(255), Lv3 varchar(255), Lv4 varchar(255), Lv5 varchar(255));
